import React, { useState } from "react";
const Stepper = () => {
  const steps = ["Project Setup", "Add Members", "Check", "Pick Template"];
  const [currentStep, setCurrentStep] = useState(1);
  const [complete, setComplete] = useState(false);

  return (
    <>
      <div className="flex justify-between bg-deep-orange-800">
        {steps?.map((step, i) => (
          <div
            key={i}
            className="relative flex flex-col justify-center items-center w-36"
          >
            {i !== steps.length - 1 && ( // Render line for all steps except the last one
              <div
                style={{
                  backgroundColor: (i + 1 < currentStep || complete) ? '#FFD600' : '#FFFFFF',
                  position: 'absolute',
                  width: '275px',
                  height: '2px',
                  right: '-235%',
                  top: '23%',
                  transform: 'translate(-50%, -50%)',
                }}
              ></div>
            )}
            <div className="w-5 h-5 mx-auto my-0 flex items-center justify-center z-10 relative rounded-full font-semibold" style={{ backgroundColor: (i + 1 < currentStep || complete) ? '#FFD600' : '#FFFFFF' }}>
            </div>
            <p className="text-white-400 font-poppins text-lg font-normal leading-7">{step}</p>
          </div>
        ))}
      </div>
      {!complete && (
        <button
          className="bg-color-gray text-lg"
          onClick={() => {
            currentStep === steps.length
              ? setComplete(true)
              : setCurrentStep((prev) => prev + 1);
          }}
        >
          {currentStep === steps.length ? "Finish" : "Next"}
        </button>
      )}
    </>
  );
};


export default Stepper;
